pragma solidity ^0.4.23;

import "../node_modules/zeppelin-solidity/contracts/ownership/Ownable.sol";
import "../node_modules/zeppelin-solidity/contracts/math/SafeMath.sol";

import "../node_modules/zeppelin-solidity/contracts/token/ERC20/DetailedERC20.sol";
import "./GenericERC20.sol";

contract BackingERC20Token is Ownable {
    using SafeMath for uint256;

    /**
     * @notice We usually require to know who are all the backholders.
     */
    address[] internal backholders;

    /**
     * @notice The backs for each backholder.
     */
    // mapping(address => uint256) internal backs;
    mapping(address => mapping(address => uint256)) internal backes;

    /**
     * @notice The accumulated rewards for each backholder.
     */
    mapping(address => uint256) internal rewards;

    // ---------- STAKES ----------

    /**
     * @notice A method for a backholder to create a back.
     * @param _back The size of the back to be created.
     */
    // function createBack(uint256 _back, GenericERC20 _token) public {
    function createBack(
        uint256 _back,
        address _address,
        GenericERC20 _token
    ) public {
        //
        // BurnableToken token = BurnableToken(_token);
        // _token.burn(_back);
        if (backes[_address][_token] == 0) addBackholder(_address);
        backes[_address][_token] = backes[_address][_token].add(_back);
    }

    /**
     * @notice A method for a backholder to remove a back.
     * @param _back The size of the back to be removed.
     */
    // function removeBack(uint256 _back, MintableToken _token) public {
    function removeBack(
        uint256 _back,
        address _address,
        GenericERC20 _token
    ) public {
        backes[_address][_token] = backes[_address][_token].sub(_back);
        if (backes[_address][_token] == 0) removeBackholder(_address);

        // _token.mint(msg.sender, _back);
    }

    /**
     * @notice A method to retrieve the back for a backholder.
     * @param _backholder The backholder to retrieve the back for.
     * @return uint256 The amount of wei backd.
     */
    function backOf(address _backholder, GenericERC20 _token)
        public
        view
        returns (uint256)
    {
        return backes[_backholder][_token];
    }

    /**
     * @notice A method to the aggregated backs from all backholders.
     * @return uint256 The aggregated backs from all backholders.
     */
    function totalBacks(GenericERC20 _token) public view returns (uint256) {
        uint256 _totalBacks = 0;
        for (uint256 s = 0; s < backholders.length; s += 1) {
            _totalBacks = _totalBacks.add(backes[backholders[s]][_token]);
        }
        return _totalBacks;
    }

    // ---------- STAKEHOLDERS ----------

    /**
     * @notice A method to check if an address is a backholder.
     * @param _address The address to verify.
     * @return bool, uint256 Whether the address is a backholder,
     * and if so its position in the backholders array.
     */
    function isBackholder(address _address)
        public
        view
        returns (bool, uint256)
    {
        for (uint256 s = 0; s < backholders.length; s += 1) {
            if (_address == backholders[s]) return (true, s);
        }
        return (false, 0);
    }

    /**
     * @notice A method to add a backholder.
     * @param _backholder The backholder to add.
     */
    function addBackholder(address _backholder) public {
        (bool _isBackholder, ) = isBackholder(_backholder);
        if (!_isBackholder) backholders.push(_backholder);
    }

    /**
     * @notice A method to remove a backholder.
     * @param _backholder The backholder to remove.
     */
    function removeBackholder(address _backholder) public {
        (bool _isBackholder, uint256 s) = isBackholder(_backholder);
        if (_isBackholder) {
            // backholders.pop();
            delete backholders[s];
            backholders[s] = backholders[backholders.length - 1];
        }
    }

    // ---------- REWARDS ----------

    /**
     * @notice A method to allow a backholder to check his rewards.
     * @param _backholder The backholder to check rewards for.
     */
    function rewardOf(address _backholder) public view returns (uint256) {
        return rewards[_backholder];
    }

    /**
     * @notice A method to the aggregated rewards from all backholders.
     * @return uint256 The aggregated rewards from all backholders.
     */
    function totalRewards() public view returns (uint256) {
        uint256 _totalRewards = 0;
        for (uint256 s = 0; s < backholders.length; s += 1) {
            _totalRewards = _totalRewards.add(rewards[backholders[s]]);
        }
        return _totalRewards;
    }

    /**
     * @notice A simple method that calculates the rewards for each backholder.
     * @param _backholder The backholder to calculate rewards for.
     */
    function calculateReward(address _backholder, GenericERC20 _token)
        public
        view
        returns (uint256)
    {
        return backes[_backholder][_token] / 100;
    }

    /**
     * @notice A method to distribute rewards to all backholders.
     */
    // function distributeRewards() public onlyOwner {
    function distributeRewards(GenericERC20 _token) public {
        for (uint256 s = 0; s < backholders.length; s += 1) {
            address backholder = backholders[s];
            uint256 reward = calculateReward(backholder, _token);
            rewards[backholder] = rewards[backholder].add(reward);
        }
    }

    /**
     * @notice A method to allow a backholder to withdraw his rewards.
     */
    function withdrawReward(MintableToken _token) public {
        uint256 reward = rewards[msg.sender];
        rewards[msg.sender] = 0;
        _token.mint(msg.sender, reward);
    }
}

