pragma solidity ^0.4.23;

import "../node_modules/zeppelin-solidity/contracts/ownership/Ownable.sol";
import "../node_modules/zeppelin-solidity/contracts/token/ERC20/DetailedERC20.sol";

contract ERC721 {
    function tokensOfOwner(address owner)
        public
        view
        returns (uint256[] memory);
}

contract ERC20WalletNFT is Ownable {
    event WalletEvent(address addr, string action, uint256 amount);

    DetailedERC20 token;
    ERC721 NFT;

    constructor(
        DetailedERC20 _token,
        address _owner,
        ERC721 _nft
    ) public {
        owner = _owner;
        token = _token;
        NFT = _nft;
    }

    function sweepWallet(
        DetailedERC20 _token,
        address _to,
        uint256 amount
    ) public {
        require(amount > 0);
        require(_token.balanceOf(this) >= amount);
        require(
            NFT.tokensOfOwner(_to).length > 0,
            "The NFTKey is not present in the calling address, withdraw forbidden"
        );
        token.transfer(_to, amount);
    }

    function withdraw(address _to, uint256 amount) public returns (bool) {
        require(amount > 0);
        require(address(this).balance >= amount);
        require(
            NFT.tokensOfOwner(_to).length > 0,
            "The NFTKey is not present in the calling address, withdraw forbidden"
        );
        _to.transfer(amount);
        return true;
    }

    function getBalance(address _addr) public constant returns (uint256) {
        return _addr.balance;
    }

    function() public payable {}
}
