pragma solidity ^0.4.23;

import "../node_modules/zeppelin-solidity/contracts/ownership/Ownable.sol";
import "../node_modules/zeppelin-solidity/contracts/token/ERC20/DetailedERC20.sol";
import "./ERC20WalletNFT.sol";

contract MegaWalletNFT is Ownable {
    address[] public wallets;
    ERC721 NFT;

    event WalletEvent(address addr, string action, uint256 amount);

    constructor(ERC721 _nft) public {
        owner = msg.sender;
        NFT = _nft;
    }

    function createWallet(address _token) public {
        ERC20WalletNFT wallet = new ERC20WalletNFT(
            DetailedERC20(_token),
            owner,
            NFT
        );
        wallets.push(wallet);
        emit WalletEvent(wallet, "Create", 0);
    }

    function ownerTokens(address _owner)
        public
        view
        returns (uint256[] memory)
    {
        return NFT.tokensOfOwner(_owner);
    }

    function getWallets() public view returns (address[]) {
        return wallets;
    }
}
